var northwindControllers=angular.module("northwindControllers",["jsonFormatter"]);northwindControllers.controller("CustomersControl",["$scope","Customers",function(n,o){o.one().get().then(function(o){for(var t in o.instances){var r=o.instances[t]["@id"];r=r.substr(r.lastIndexOf("/")+1),o.instances[t]["@id"]=r}n.customers=o.instances})}]),northwindControllers.controller("CustomerDetailControl",["$scope","$routeParams","Customers",function(n,o,t){t.one(o.instance).get().then(function(o){n.customer=o})}]);