var northwindControllers=angular.module("northwindControllers",["jsonFormatter"]);northwindControllers.controller("CustomersControl",["$scope","Customers",function(n,t){t.instances().get().then(function(t){for(var o in t.instances){var r=t.instances[o]["@id"];r=r.substr(r.lastIndexOf("/")+1),t.instances[o]["@id"]=r}n.customers=t.instances})}]),northwindControllers.controller("CustomerDetailControl",["$scope","$routeParams","Customers",function(n,t,o){o.instance(t.instance).get().then(function(t){n.customer=t.plain()})}]);